sect2.programlisting:
// StartEnd.c
#include <Rts.h>

extern void __stginit_Adder(void);

void HsStart()
{
   int argc = 1;
   char* argv[] = {"ghcDll", NULL}; // argvはNULLで終わっていなければならない

   // Haskellのランタイムを初期化する
   char** args = argv;
   hs_init(&argc, &args);

   // Haskellに全ての根モジュールを通知する
   hs_add_root(__stginit_Adder);
}

void HsEnd()
{
   hs_exit();
}

sect3.programlisting:
// Tester.cpp
#include "HsFFI.h"
#include "Adder_stub.h"
#include <stdio.h>

extern "C" {
    void HsStart();
    void HsEnd();
}

int main()
{
    HsStart();
    // これで、DLLの関数を安全に呼べる
    printf("12 + 5 = %i\n", adder(12,5))    ;
    HsEnd();
    return 0;
}
